.new-products {
    position: relative;
    border-radius: 25px;
    padding: 22px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    overflow: hidden;
    background: linear-gradient(135deg, #f8f8f8, #e6e6e6);
    animation: backgroundAnimation 5s ease infinite; /* Th\xEAm animation cho nền mặc định */
    transition: box-shadow 0.5s ease;
    margin-top: 15px;
}

/* Định nghĩa animation cho nền */
@keyframes backgroundAnimation {
    0% {
        background: linear-gradient(135deg, #f8f8f8, #e6e6e6);
    }
    50% {
        background: linear-gradient(135deg, #ff9a9e, #fad0c4);
    }
    100% {
        background: linear-gradient(135deg, #f8f8f8, #e6e6e6);
    }
}
.new-products:hover {
    box-shadow: 0 8px 16px rgba(255, 140, 180, 0.3), 0 4px 8px rgba(0, 0, 0, 0.1);
    animation: backgroundHoverAnimation 2s ease infinite; /* Gọi animation cho nền khi hover */
}
/* Định nghĩa animation cho nền khi hover */
@keyframes backgroundHoverAnimation {
    0% {
        background: linear-gradient(135deg, #ff9a9e, #fad0c4);
    }
    50% {
        background: linear-gradient(135deg, #fbc2eb, #ff9a9e);
    }
    100% {
        background: linear-gradient(135deg, #ff9a9e, #fad0c4);
    }
}
.new-products::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(255, 255, 255, 0.1), transparent 70%);
    transition: transform 0.5s ease;
    transform: scale(0); /* Ẩn hiệu ứng trước khi hover */
}
.new-products:hover::before {
    transform: scale(1); /* K\xEDch hoạt hiệu ứng \xE1nh s\xE1ng lan tỏa */
}
.hot-products {
  background: linear-gradient(rgb(248, 62, 96), rgb(254, 80, 72));
  border-radius: 25px;
  padding: 22px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  animation: backgroundShift 5s ease infinite;
  margin-top: 15px;
}
.gradient-text {
  background: linear-gradient(45deg, red, orange, yellow, green, blue, indigo, violet, red);
  background-size: 400% 400%;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  font-weight: bold;
  font-size: 3em;
  text-align: center;
  animation: gradientShift 8s ease-in-out infinite, blink 1s step-start infinite;
  text-decoration: none;
  margin: 15px;
}
@keyframes gradientShift {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}
@keyframes blink {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.8; }
}
.product-card {
  flex: 0 0 calc(33.33% - 20px); 
  margin: 19px; 
  transition: transform 0.3s, box-shadow 0.3s;
  border-radius: 10px;
  overflow: hidden;
  background: #ffffff;
  position: relative;
}
.product-card:hover {
  transform: translateY(-10px);
  box-shadow: 0 4px 8px rgba(0, 0, 0, 1), 0 0 15px rgba(255, 255, 255, 1);
}
.product-card img {
  border-radius: 10px 10px 0 0;
}
.product-card figcaption {
  padding: 10px;
  background-color: #fff;
  text-align: center;
}
.hot-products .product-card figcaption .price,
.new-products .product-card figcaption .price {
  color: red; /* M\xE0u đỏ */
  font-weight: bold; 
  font-size: 1.2em; 
}
.new-products a,
.hot-products a {
  text-decoration: none;
  color: #333; 
  font-weight: bold;
}
.promo-info {
  font-weight:bold;
  display: none;
  position: absolute;
  bottom: 150px;
  left: 50%;  /* Center horizontally */
  transform: translateX(-50%);  /* Adjust for centering */
  background: rgba(255, 255, 255, 0.8);
  border: 3px solid #006400;
  border-radius: 10px;
  padding: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  z-index: 10;
  width: 320px;  /* Adjust the width as needed */
}
.promo-info .promo-item {
  display: flex;
  align-items: center;
  margin-bottom: 5px;
  font-size: 12px;
  color: #333;
}
.promo-info .promo-item::before {
  content: 'KM';
  display: inline-block;
  font-size: 10px;
  color: white;
  background: orange;
  border-radius: 3px;
  padding: 2px 5px;
  margin-right: 5px;
}
.product-card:hover .promo-info {
  display: block;
  outline: 1px solid #ddd;
}
